import './App.css'
import CartWidget from './components/CartWidget'
import ComponenteContenedor from './components/ItemListContainer/ComponenteContenedor'
import Nav from './components/Nav'
import TituloApp from './components/TituloApp'
import { useState } from 'react';




function App() {

const style = { backgroundColor:'blue',   color:'white' } 

const [cont, setCont] = useState(0);

const sumarContador = () => {
  setCont(cont + 1);
  console.log(cont);

};

const restarContador = () => {
  setCont(cont - 1);
  console.log(cont);

};


  return (
    <div className="App"
     style={ style }

     >
       <div>
        <span>{cont}</span>
        <button onClick={sumarContador}>click</button>

       </div>
       <div>
        <span>{cont}</span>
        <button onClick={restarContador}>click</button>

       </div>



    <TituloApp/>
    <Nav/>
    <CartWidget/>
    <ComponenteContenedor listaDeProductos= 'Bienvenidos, esta es la lista de productos que encontrara en nuestro local: ' />





    </div>
  )
}

export default App
